# Epic Title: Secure Login System with MFA

from sqlalchemy.orm import Session
from backend.authentication.models.account import Account
from typing import Optional

class AccountRepository:
    def __init__(self, db: Session):
        self.db = db

    def get_account_by_email(self, email: str) -> Optional[Account]:
        return self.db.query(Account).filter(Account.email == email).first()

    def create_account(self, name: str, email: str, password: str) -> Account:
        account = Account(name=name, email=email)
        account.set_password(password)
        self.db.add(account)
        self.db.commit()
        self.db.refresh(account)
        return account

    def validate_login(self, email: str, password: str) -> Optional[Account]:
        account = self.get_account_by_email(email)
        if account and account.check_password(password):
            return account
        return None

# Epic Title: Secure Login System with MFA

from sqlalchemy.orm import Session
from backend.authentication.repositories.mfa_repository import MFARepository
import random
import string

class MFAService:
    def __init__(self, mfa_repository: MFARepository):
        self.mfa_repository = mfa_repository

    def generate_token(self) -> str:
        return ''.join(random.choices(string.ascii_uppercase + string.digits, k=6))

    def create_mfa_token(self, db: Session, account_id: int) -> dict:
        token = self.generate_token()
        mfa_token = self.mfa_repository.create_token(account_id, token)
        return {"success": True, "token": mfa_token.token}

    def validate_mfa_token(self, db: Session, account_id: int, token: str) -> dict:
        is_valid = self.mfa_repository.validate_token(account_id, token)
        return {"success": is_valid}

